/*
 * GET edit page.
 */
var path = require('path');
var logger = require('log4js').getLogger("studio-edit");

// GET /edit
exports.edit = function(synchroStudio, appName, req, res)
{
    var apiProcessor = synchroStudio.getApiProcessor(appName);
    var moduleStore = synchroStudio.getAppModuleStoreForPath(appName);

    var page = null;
    var code = "";
    if (req.query["page"])
    {
        page = req.query["page"];
    }

    if (page)
    {
        code = moduleStore.getModuleSource(page);
    }

    var files = moduleStore.listModules();
    var debugPort = apiProcessor.debugPort;
    var modulePathPrefix = synchroStudio.config.get('APP_ROOT_PATH') + "/" + appName + "/";

    synchroStudio.render('sandbox', { title: 'Synchro', modulePathPrefix: modulePathPrefix, code: code, page: page, files: files, debugPort: debugPort }, res);
};

// GET /module
exports.loadModule = function(synchroStudio, appName, req, res)
{
    var moduleStore = synchroStudio.getAppModuleStoreForPath(appName);
    var moduleName = req.query["module"];

    logger.info("Load module: " + moduleName);
    var result = { };

    if (moduleName)
    {
        result.status = "OK";
        result.message = "Module source found";
        result.source = moduleStore.getModuleSource(moduleName);
    }
    else
    {
        result.status = "Fail";
        result.message = "No module specified";
    }
    res.send(result);
};

// POST /module
exports.saveModule = function(synchroStudio, appName, req, res)
{
    var apiProcessor = synchroStudio.getApiProcessor(appName);
    var moduleStore = synchroStudio.getAppModuleStoreForPath(appName);

    logger.info("Save module");
    var result = { };

    if (req.body["module"] && req.body["source"])
    {
        var moduleName = req.body["module"];
        var source = req.body["source"];

        var putResult = moduleStore.putModuleSource(moduleName, source);

        var reloadResult = apiProcessor.reloadModule(moduleName);
        if (reloadResult["error"]) 
        {
            result.status = "Fail";
            result.message = "Unable to deploy module, reason: " + reloadResult["error"];
        }
        else
        {
            result.status = "OK";
            result.message = "Module updated and deployed";
        } 
    }
    else
    {
        result.status = "Fail";
        result.message = "Module source not saved";
    }

    res.send(result);
};