// The Synchro Web client server can be run directly using this module, though it is intended to
// be used in production via a Synchro Server instance that forwards requests via the SynchroWeb
// mechanism (in index.js).
//
// Note that if running as a standalone server using this module, you will likely need to enable
// CORS (cross-origin reference sharing) in your browser.
//
// https://github.com/expressjs/cors -- !!! Try this here!
//
var express = require('express');
var app = express();

var SynchroWeb = require('./index');
var synchroWeb = new SynchroWeb({}, "", "http://localhost:1337/api");

synchroWeb.addMiddleware(app);
synchroWeb.addRoutes(app);

app.listen(3000, function () 
{
    console.log('SynchroWebClient server listening on port 3000!');
});
