var logger = require('log4js').getLogger("web-progress-ring-wrapper");

var WebControlWrapper = require('./web-control-wrapper');

var binding = require('../core/binding');
var CommandInstance = require('../core/command-instance');

var WebProgressRingWrapper = function(parent, bindingContext, controlSpec)
{
    var self = this;

    this.base = WebControlWrapper;
    this.base(parent, bindingContext, controlSpec);
    logger.info("ProgressRing control created");

    var progressRing = document.createElement("progress");
    progressRing.classList.add("synchro-progressring");

    this._control = progressRing;
}
WebProgressRingWrapper.prototype = Object.create(WebControlWrapper.prototype);

module.exports = WebProgressRingWrapper;
