var logger = require('log4js').getLogger("web-rectangle-wrapper");

var WebControlWrapper = require('./web-control-wrapper');

var binding = require('../core/binding');
var CommandInstance = require('../core/command-instance');

var WebRectangleWrapper = function(parent, bindingContext, controlSpec)
{
    var self = this;

    this.base = WebControlWrapper;
    this.base(parent, bindingContext, controlSpec);
    logger.info("Rectangle control created");

    this._control = document.createElement("div");
    this._control.classList.add("synchro-rectangle");

    this._control.style.display = "inline-block";
    this.processElementProperty(controlSpec, "border", function(value) { self._control.style.borderColor = self.toColor(value) });
    this.processElementProperty(controlSpec, "borderThickness", function(value) 
    { 
        self._control.style.borderStyle = "solid";
        self._control.style.borderWidth = self.toDeviceUnits(value);
    });
    this.processElementProperty(controlSpec, "cornerRadius", function(value) { self._control.style.borderRadius = self.toDeviceUnits(value) });
    this.processElementProperty(controlSpec, "fill", function(value) 
    { 
        self._control.style.backgroundColor = self.toColor(value);
    });

    var bindingSpec = binding.getCanonicalBindingSpec(controlSpec, CommandInstance.commandNames.OnTap, WebRectangleWrapper.commands);
    this.processCommands(bindingSpec, WebRectangleWrapper.commands);

    if (this.getCommand(CommandInstance.commandNames.OnTap) != null)
    {
        // Attach a handler
        logger.info("Adding rectangle onTap handler");
        this._control.addEventListener("click", this.OnTap.bind(this), false);
    }
}

WebRectangleWrapper.prototype = Object.create(WebControlWrapper.prototype);

WebRectangleWrapper.commands = [ CommandInstance.commandNames.OnTap ];

WebRectangleWrapper.prototype.OnTap = function()
{
    logger.info("Rectangle tapped");
    var command = this.getCommand(CommandInstance.commandNames.OnTap);
    if (command != null)
    {
        logger.info("Rectangle tapped with command: %s", command.getCommand());
        this._stateManager.sendCommandRequestAsync(command.getCommand(), command.getResolvedParameters(this._bindingContext));
    }
}

module.exports = WebRectangleWrapper;
