var assert = require('assert');

var SynchroApp = require('../client/core/synchro-app');

describe("SynchroApp", function () 
{
    it("should return correct app name and description", function()
    {
        var app = new SynchroApp("http://foo.com", 
        {
            name: "test-app",
            description: "Test App"
        });

        assert.equal("test-app", app.getName());
        assert.equal("Test App", app.getDescription());
    });
});
